
public class P11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=5;i++) {
			for(int k=1;k<=30;k++) {
				System.out.print(" ");
			}
			for(int j=1;j<=i;j++) {

				System.out.print(">");

			}
			System.out.println();
		}
		for(int i=1;i<=5;i++) {
			for(int k=1;k<=i;k++) {
				System.out.print(" ");
			}
			for(int j=1;j<=i;j++) {

				System.out.print(">");

			}
			System.out.println();
		}
		for(int i=1;i<=1;i++) {
			for(int j=1;j<=35;j++) {
			System.out.print(">");
		}
		
		System.out.println();
		}
	
	for(int i=1;i<=5;i++) {
		for(int k=5;k>=-24;k--) {
			System.out.print(" ");
		}
		for(int j=5;j>=i;j--) {
			System.out.print(">");
			
		}
		System.out.println();
	}
	for(int i=1;i<=5;i++) {
		for(int k=5;k>=i;k--) {
			System.out.print(" ");
		}
		for(int j=5;j>=i;j--) {
			System.out.print(">");
			
		}
		System.out.println();
	}

}
}
